CREATE FUNCTION EncryptData (@plaintext VARCHAR(50)) RETURNS VARBINARY(255)\nAS\nBEGIN\n    DECLARE @key VARBINARY(16) = 0x1234567890abcdef\n    DECLARE @encrypted VARBINARY(255)\n    SET @encrypted = CONVERT(VARBINARY(255), @plaintext)\n    DECLARE @i INT\n    SET @i = 1\n    WHILE @i <= LEN(@plaintext)\n    BEGIN\n        SET @encrypted = @encrypted + CONVERT(VARBINARY(1), ASCII(SUBSTRING(@plaintext, @i, 1)) ^ SUBSTRING(@key, @i % 16, 1))\n        SET @i = @i + 1\n    END\n    RETURN @encrypted\nEND\n\nCREATE FUNCTION DecryptData (@encrypted VARBINARY(255)) RETURNS VARCHAR(50)\nAS\nBEGIN\n    DECLARE @key VARBINARY(16) = 0x1234567890abcdef\n    DECLARE @decrypted VARCHAR(50)\n    SET @decrypted = ''\n    DECLARE @i INT\n    SET @i = 1\n    WHILE @i <= LEN(@encrypted)\n    BEGIN\n        SET @decrypted = @decrypted + CHAR(CONVERT(INT, SUBSTRING(@encrypted, @i, 1)) ^ SUBSTRING(@key, @i % 16, 1))\n        SET @i = @i + 1\n    END\n    RETURN @decrypted\nEND