CREATE TABLE search_results (\n  id SERIAL PRIMARY KEY,\n  search_query_id INTEGER NOT NULL,\n  result_type VARCHAR(50) NOT NULL CHECK (result_type IN ('company', 'service', ...)),\n  result_id INTEGER,  -- foreign key to the relevant table (e.g., companies, services)\n  position INTEGER NOT NULL,  -- result position (1-based indexing)\n  FOREIGN KEY (search_query_id) REFERENCES search_queries(id)\n);