-- Create a custom script, e.g., custom_awrrpt.sql\n-- Set the num_sql variable to 25\n DEFINE num_sql 25\n\n-- Use the modified query from the original script\n SELECT /*+ RULE */ \n        sql_id, \n        substr(sql_text,1,80) sql_text, \n        ...\n  FROM ( SELECT /*+ RULE */ \n                sql_id, \n                sql_text, \n                ...\n          FROM dba_hist_sqltext t \n          WHERE t.dbid = :dbid \n          AND t.snap_id BETWEEN :bid AND :eid \n          ORDER BY t.elap EXECS DESC \n       ) \n WHERE ROWNUM <= &num_sql ;\n\n-- Run the custom script using SQL*Plus\nSQL> @custom_awrrpt.sql