SELECT \n  q1.product_name,\n  q1.price,\n  q1.quantity,\n  @cumulative_sum:=@cumulative_sum + q1.quantity AS cumulative_sum\nFROM \n  (SELECT \n     product_name,\n     price,\n     quantity\n   FROM \n     quantity\n   WHERE \n     product_name = 'DELL'\n   ORDER BY \n     date DESC, \n     position) AS q1\n  JOIN (SELECT @cumulative_sum:=0) AS init\n  LEFT JOIN (\n    SELECT \n      product_name,\n      price,\n      quantity\n    FROM \n      quantity\n    WHERE \n      product_name = 'OTHER_PRODUCT_NAME'\n  ) AS q2 ON q2.product_name = 'OTHER_PRODUCT_NAME'\nWHERE \n  q1.quantity + q2.quantity = 35;