CREATE PROCEDURE MyProcedure (@input int, @Result1 TABLE (id int, name varchar(50)) OUTPUT, @Result2 TABLE (id int, value decimal(10, 2)) OUTPUT)\nAS\nBEGIN\n    -- common code that generates two result sets\n    INSERT INTO @Result1 (id, name)\n    SELECT id, name FROM mytable WHERE id = @input\n\n    INSERT INTO @Result2 (id, value)\n    SELECT id, value FROM myothertable WHERE id = @input\nEND