CREATE TABLE #temp(\n    [count] int\n  , [Time] nvarchar(50)\n)\n\nDECLARE @DateNow DATETIME, @i int\n\nSET @DateNow = '00:00'\nSET @i = 1;\n\nWHILE (@i < 1440)\n    BEGIN\n        SET @DateNow = DATEADD(minute, 1, @DateNow)\n        INSERT INTO #temp ([count], [Time]) VALUES (0, @DateNow)\n        SET @i = @i + 1\n    END\n\nSELECT [count], FORMAT([Time], 'HH:mm') AS [Time] FROM #temp\n\nDROP TABLE #temp