CREATE PROCEDURE ComputeValue\nAS\nBEGIN\n    DECLARE @cursor CURSOR;\n    DECLARE @table1_GUID nvarchar(100), @table2_GUID nvarchar(100), ...;\n\n    SET @cursor = CURSOR FOR\n        SELECT table1.GUID, table2.GUID, ...\n        FROM table1\n        INNER JOIN table2 ON table1.GUID = table2.GUID\n        ...;\n\n    OPEN @cursor;\n\n    FETCH NEXT FROM @cursor INTO @table1_GUID, @table2_GUID, ...;\n\n    WHILE @@FETCH_STATUS = 0\n    BEGIN\n        -- Compute values involving data from multiple tables\n        FETCH NEXT FROM @cursor INTO @table1_GUID, @table2_GUID, ...;\n    END\n\n    CLOSE @cursor;\n    DEALLOCATE @cursor;\nEND;