CREATE TABLE questions (\n  id INT PRIMARY KEY AUTO_INCREMENT,\n  title VARCHAR(255) NOT NULL,\n  content TEXT NOT NULL,\n  user_id INT NOT NULL,\n  created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n  votes INT DEFAULT 0\n);\n\nCREATE TABLE votes (\n  id INT PRIMARY KEY AUTO_INCREMENT,\n  question_id INT NOT NULL,\n  user_id INT NOT NULL,\n  created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP\n);