WITH RECURSIVE tag_subsets AS (\n  SELECT tag_id, tag, 1 AS level\n  FROM (\n    SELECT t.tag_id, t.tag\n    FROM Post_tag_nn pt\n    JOIN Tags t ON pt.tag_id = t.tag_id\n    WHERE pt.post_id = ?  // replace ? with the specific post_id\n  ) AS initial_tags\n  UNION ALL\n  SELECT ts.tag_id, ts.tag, level + 1\n  FROM tag_subsets ts\n  JOIN (\n    SELECT t.tag_id, t.tag\n    FROM Post_tag_nn pt\n    JOIN Tags t ON pt.tag_id = t.tag_id\n    WHERE pt.post_id = ?  // replace ? with the specific post_id\n  ) AS next_tags ON ts.tag_id < next_tags.tag_id\n)\nSELECT * FROM tag_subsets;