-- First select statement\nSELECT val1, val3, val5, val2, val4\nINTO #temp\nFROM your_table;\n\n-- Second select statement with dynamic order\nSELECT *\nFROM your_other_table\nORDER BY \n    CASE \n        WHEN your_column = (SELECT val1 FROM #temp) THEN 1\n        WHEN your_column = (SELECT val3 FROM #temp) THEN 2\n        WHEN your_column = (SELECT val5 FROM #temp) THEN 3\n        WHEN your_column = (SELECT val2 FROM #temp) THEN 4\n        WHEN your_column = (SELECT val4 FROM #temp) THEN 5\n    END;