BEGIN CATCH\n    IF EXISTS (SELECT 1 FROM @updatedRows)\n    BEGIN\n        DECLARE @errorLoanid INT\n        SELECT @errorLoanid = loanid FROM @updatedRows\n        PRINT 'Error occurred while updating loanid: ' + CONVERT(VARCHAR, @errorLoanid)\n    END\n    ELSE\n    BEGIN\n        PRINT 'Error occurred before any rows were updated'\n    END\n    PRINT ERROR_MESSAGE()\nEND CATCH