CREATE INDEX idx_perm_action ON permissions (perm_action);\nCREATE INDEX idx_action_id ON actions (action_id);\nCREATE INDEX idx_perm_role ON permissions (perm_role);\nCREATE INDEX idx_action_name_module ON actions (action_name, action_module);\n\nSELECT perm_id\nFROM permissions\nLEFT JOIN actions\n    ON permissions.perm_action = actions.action_id\nWHERE perm_role = 1\n    AND action_name = 'add'\n    AND action_module = 'employee';