-- Create a new database user\nCREATE USER LimitedUser FOR LOGIN = 'LimitedUser' WITH DEFAULT_SCHEMA = 'dbo';\n\n-- Grant SELECT permission on specific views\nGRANT SELECT ON VIEW::[dbo].[View1] TO LimitedUser;\nGRANT SELECT ON VIEW::[dbo].[View2] TO LimitedUser;\n\n-- Deny all other permissions on the database\nDENY DELETE, INSERT, UPDATE, EXECUTE, ALTER, CONTROL, TAKE OWNERSHIP ON DATABASE::MyDatabase TO LimitedUser;