-- Assuming you have a linked server named "ClientServer" set up on your main server\n\n-- Insert new records from the server to the client\nINSERT INTO [ClientServer].[YourDatabase].[dbo].[YourTable] (column1, column2)\nSELECT column1, column2\nFROM YourDatabase.dbo.YourTable\nWHERE NOT EXISTS (\n    SELECT 1\n    FROM [ClientServer].[YourDatabase].[dbo].[YourTable]\n    WHERE column1 = YourDatabase.dbo.YourTable.column1\n);\n\n-- Update existing records on the client\nUPDATE c\nSET column2 = s.column2\nFROM [ClientServer].[YourDatabase].[dbo].[YourTable] c\nINNER JOIN YourDatabase.dbo.YourTable s ON c.column1 = s.column1\nWHERE c.column2 <> s.column2;\n\n-- Delete records on the client that were deleted on the server\nDELETE c\nFROM [ClientServer].[YourDatabase].[dbo].[YourTable] c\nWHERE NOT EXISTS (\n    SELECT 1\n    FROM YourDatabase.dbo.YourTable\n    WHERE column1 = c.column1\n);