-- Enable TDE\nALTER DATABASE [mydatabase] SET ENCRYPTION ON;\n\n-- Create a certificate for column-level encryption\nCREATE CERTIFICATE [mycert] ENCRYPTION BY PASSWORD = 'mystrongpassword';\n\n-- Encrypt a column\nALTER TABLE [mytable] ALTER COLUMN [mycolumn] ADD ENCRYPTION (\n    COLUMN_ENCRYPTION_KEY = [mycert],\n    ENCRYPTION_TYPE = 'DETERMINISTIC',\n    ALGORITHM = 'AEAD_AES_256_CBC_HMAC_SHA_256'\n);