SELECT \n    s.subscription_id,\n    f.feed_id,\n    f.feed_type,\n    -- Use feed_type to generate the correct link\n    CASE f.feed_type\n        WHEN 'public' THEN CONCAT('/public/', f.feed_id)\n        WHEN 'private' THEN CONCAT('/private/', f.feed_id)\n        WHEN 'generated' THEN CONCAT('/generated/', f.feed_id)\n    END AS link\nFROM subscriptions s\nJOIN feeds f ON s.feed_id = f.feed_id;