SELECT \n  p.pubid,\n  p.keywords,\n  pt.type,\n  a.article_specific_column,\n  b.book_specific_column,\n  p.paper_specific_column\nFROM \n  publications p\n  INNER JOIN publication_types pt ON p.pubid = pt.pubid\n  LEFT JOIN articles a ON p.pubid = a.pubid\n  LEFT JOIN books b ON p.pubid = b.pubid\n  LEFT JOIN papers p ON p.pubid = p.pubid\nWHERE \n  p.keywords LIKE '%search_keyword%';