CREATE TABLE feeds (\n    feed_id INT PRIMARY KEY,\n    feed_type VARCHAR(20) CHECK(feed_type IN ('public', 'private', 'generated'))\n    -- Other common attributes of feeds\n);\n\nCREATE TABLE public_feeds (\n    feed_id INT PRIMARY KEY,\n    -- Attributes specific to public feeds\n    FOREIGN KEY (feed_id) REFERENCES feeds(feed_id)\n);\n\nCREATE TABLE private_feeds (\n    feed_id INT PRIMARY KEY,\n    -- Attributes specific to private feeds\n    FOREIGN KEY (feed_id) REFERENCES feeds(feed_id)\n);\n\nCREATE TABLE generated_feeds (\n    feed_id INT PRIMARY KEY,\n    -- Attributes specific to generated feeds\n    FOREIGN KEY (feed_id) REFERENCES feeds(feed_id)\n);\n\nCREATE TABLE subscriptions (\n    subscription_id INT PRIMARY KEY,\n    user_id INT,\n    feed_id INT,\n    FOREIGN KEY (feed_id) REFERENCES feeds(feed_id)\n);