SELECT * FROM (\n  SELECT d.DataId, \n         (SELECT [Name] FROM Category WHERE CategoryId = d.CategoryId) AS 'Category', \n         d.Description, \n         d.CompanyName, \n         d.City, \n         d.CategoryId,\n         d.CreatedOn, \n         d.Rank, \n         d.voteCount, \n         d.commentCount, \n         d.viewCount,\n         k.RANK AS Relevance\n  FROM Data d \n  INNER JOIN FREETEXTTABLE(Keyword, Keyword, @SearchQ) k\n    ON d.DataId = k.[KEY]\n  WHERE d.CategoryId=@CategoryId AND d.IsSearch=1 AND d.IsApproved=1\n) AS Results\nORDER BY Relevance DESC;