DECLARE @dayName VARCHAR(10) = 'Tuesday'\nDECLARE @date DATETIME\n\n-- Get the date of the day name\nSET @date = (SELECT TOP 1 DATEADD(DAY, \n                                   (CASE \n                                     WHEN DATENAME(WEEKDAY, 0) = @dayName THEN 0\n                                     WHEN DATENAME(WEEKDAY, 1) = @dayName THEN 1\n                                     WHEN DATENAME(WEEKDAY, 2) = @dayName THEN 2\n                                     WHEN DATENAME(WEEKDAY, 3) = @dayName THEN 3\n                                     WHEN DATENAME(WEEKDAY, 4) = @dayName THEN 4\n                                     WHEN DATENAME(WEEKDAY, 5) = @dayName THEN 5\n                                     WHEN DATENAME(WEEKDAY, 6) = @dayName THEN 6\n                                   END), \n                                   '19000101'))\n\n-- Get the integer representation of the day\nSELECT DATEPART(WEEKDAY, @date)