CREATE OR REPLACE FUNCTION fnc_getReadableSize(p_blob BLOB) RETURN VARCHAR2 IS\n  v_size NUMBER;\n  v_unit VARCHAR2(10);\nBEGIN\n  v_size := DBMS_LOB.GETLENGTH(p_blob);\n  IF v_size < 1024 THEN\n    v_unit := 'B';\n  ELSIF v_size < 1024*1024 THEN\n    v_size := ROUND(v_size / 1024, 2);\n    v_unit := 'KB';\n  ELSIF v_size < 1024*1024*1024 THEN\n    v_size := ROUND(v_size / (1024*1024), 2);\n    v_unit := 'MB';\n  ELSE\n    v_size := ROUND(v_size / (1024*1024*1024), 2);\n    v_unit := 'GB';\n  END IF;\n  RETURN TO_CHAR(v_size) || ' ' || v_unit;\nEND fnc_getReadableSize;\n/