CREATE TABLE UniqueIds (\n    Id INT IDENTITY(1,1) PRIMARY KEY\n);\n\nALTER TABLE Groups\nADD UniqueId INT NOT NULL;\nALTER TABLE Units\nADD UniqueId INT NOT NULL;\nALTER TABLE Staff\nADD UniqueId INT NOT NULL;\n\nALTER TABLE Groups\nADD CONSTRAINT FK_Groups_UniqueIds FOREIGN KEY (UniqueId) REFERENCES UniqueIds(Id);\nALTER TABLE Units\nADD CONSTRAINT FK_Units_UniqueIds FOREIGN KEY (UniqueId) REFERENCES UniqueIds(Id);\nALTER TABLE Staff\nADD CONSTRAINT FK_Staff_UniqueIds FOREIGN KEY (UniqueId) REFERENCES UniqueIds(Id);