SELECT \n  dbo_porel.jobnum, \n  dbo_joboper.opcode, \n  dbo_porel.jobseqtype, \n  dbo_opmaster.shortchar01, \n  dbo_porel.ponum, \n  dbo_porel.poline, \n  dbo_podetail.unitcost\nFROM \n  dbo_porel\n  INNER JOIN dbo_joboper \n    ON (dbo_porel.jobnum = dbo_joboper.jobnum) \n    AND (dbo_porel.jobseq = dbo_joboper.oprseq) \n    AND (dbo_porel.assemblyseq = dbo_joboper.assemblyseq)\n  INNER JOIN dbo_opmaster \n    ON dbo_joboper.opcode = dbo_opmaster.opcode\n  LEFT JOIN dbo_podetail \n    ON (dbo_porel.poline = dbo_podetail.poline) \n    AND (dbo_porel.ponum = dbo_podetail.ponum)\nWHERE \n  (dbo_porel.jobnum = "367000003")