CREATE TABLE buildings (\n  id INT PRIMARY KEY,\n  location VARCHAR(255),\n  name VARCHAR(255),\n  type VARCHAR(20)  -- e.g., 'house', 'office'\n);\n\nCREATE TABLE houses (\n  id INT PRIMARY KEY,\n  building_id INT,\n  num_rooms INT,\n  num_bathrooms INT,\n  FOREIGN KEY (building_id) REFERENCES buildings(id)\n);\n\nCREATE TABLE offices (\n  id INT PRIMARY KEY,\n  building_id INT,\n  offices INT,\n  sq_foot INT,\n  FOREIGN KEY (building_id) REFERENCES buildings(id)\n);