SELECT \n  category.category_name, \n  SUM(equipment.total_stock - equipment.stock_out) AS Current_Stock, \n  SUM(equipment.stock_out) AS Total_Stock_Out\nFROM \n  EQUIPMENT, \n  CATEGORY\nWHERE \n  EQUIPMENT.CATEGORY_ID = CATEGORY.CATEGORY_ID\n  AND category.category_name = 'Power Tools'\nGROUP BY \n  category.category_name