WITH cat_sales AS (\n  SELECT \n    a.AnimalID, \n    a.Name, \n    a.Category, \n    (s.date - ao.ReceivingDate) AS DaysToSell\n  FROM \n    Sale s\n  INNER JOIN SaleAnimal sa ON s.SaleID = sa.SaleID\n  INNER JOIN Animal a ON sa.AnimalID = a.AnimalID\n  INNER JOIN AnimalOrderItem aoi ON a.AnimalID = aoi.AnimalID\n  INNER JOIN AnimalOrder ao ON aoi.OrderID = ao.OrderID\n  WHERE a.Category = 'Cat'\n)\nSELECT \n  AnimalID, \n  Name, \n  Category, \n  DaysToSell\nFROM \n  cat_sales\nWHERE \n  DaysToSell > (SELECT AVG(DaysToSell) FROM cat_sales)\nORDER BY \n  DaysToSell DESC;