CREATE PROCEDURE spMyStoredProcedure\n    @List varchar(255)\nAS\nBEGIN\n    DECLARE @sql nvarchar(max) = N'SELECT ID FROM YourTable WHERE ';\n    DECLARE @params nvarchar(max) = N'';\n\n    -- Split the @List parameter into individual values\n    DECLARE @values TABLE (value int);\n    INSERT INTO @values (value)\n    SELECT value FROM STRING_SPLIT(@List, ',');\n\n    -- Build the dynamic SQL query\n    SELECT @sql += ' OR col' + CONVERT(nvarchar(2), value) + ' = 1'\n    FROM @values;\n\n    -- Remove the leading ' OR '\n    SET @sql = STUFF(@sql, 1, 4, '');\n\n    -- Execute the dynamic SQL query\n    EXEC sp_executesql @sql;\nEND;