CREATE TABLE posts (\n  id INT PRIMARY KEY,\n  content TEXT,\n  owner_id INT,\n  date DATE,\n  notes_id INT\n);\n\nCREATE TABLE tags (\n  id INT PRIMARY KEY,\n  name VARCHAR(255),\n  notes_id INT\n);\n\nCREATE TABLE post_tags (\n  post_id INT,\n  tag_id INT,\n  owner_id INT,\n  created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n  PRIMARY KEY (post_id, tag_id),\n  FOREIGN KEY (post_id) REFERENCES posts(id),\n  FOREIGN KEY (tag_id) REFERENCES tags(id)\n);