CREATE OR REPLACE TRIGGER trg_insert\nAFTER INSERT ON your_table\nFOR EACH ROW\nBEGIN\n  INSERT INTO AUDIT_TRAIL (TABLE_NAME, RECORD_ID, TRANSACTION_TYPE, CHANGE_DATE, NEW_VALUE, USER_ID)\n  VALUES ('your_table', :NEW.id, 'INSERT', SYSTIMESTAMP, :NEW.column1 || ',' || :NEW.column2, :NEW.user_id);\nEND;\n\nCREATE OR REPLACE TRIGGER trg_update\nAFTER UPDATE ON your_table\nFOR EACH ROW\nBEGIN\n  INSERT INTO AUDIT_TRAIL (TABLE_NAME, RECORD_ID, TRANSACTION_TYPE, CHANGE_DATE, OLD_VALUE, NEW_VALUE, USER_ID)\n  VALUES ('your_table', :NEW.id, 'UPDATE', SYSTIMESTAMP, :OLD.column1 || ',' || :OLD.column2, :NEW.column1 || ',' || :NEW.column2, :NEW.user_id);\nEND;\n\nCREATE OR REPLACE TRIGGER trg_delete\nAFTER DELETE ON your_table\nFOR EACH ROW\nBEGIN\n  INSERT INTO AUDIT_TRAIL (TABLE_NAME, RECORD_ID, TRANSACTION_TYPE, CHANGE_DATE, OLD_VALUE, USER_ID)\n  VALUES ('your_table', :OLD.id, 'DELETE', SYSTIMESTAMP, :OLD.column1 || ',' || :OLD.column2, :OLD.user_id);\nEND;