SELECT \n  ps.player_id,\n  ps.position,\n  ps.number,\n  p.name,\n  p.surname,\n  COALESCE(t.tries, 0) AS triesNo,\n  COALESCE(c.conversions, 0) AS conversionsNo,\n  COALESCE(pen.penalties, 0) AS penaltiesNo,\n  COALESCE(dg.dropgoals, 0) AS dropgoalsNo\nFROM \n  players ps\n  LEFT JOIN player p ON ps.player_id = p.player_id\n  LEFT JOIN (\n    SELECT player_id, COUNT(*) AS tries\n    FROM tries\n    WHERE game_id = '$game_id'\n    GROUP BY player_id\n  ) t ON ps.player_id = t.player_id\n  LEFT JOIN (\n    SELECT player_id, COUNT(*) AS conversions\n    FROM conversions\n    WHERE game_id = '$game_id'\n    GROUP BY player_id\n  ) c ON ps.player_id = c.player_id\n  LEFT JOIN (\n    SELECT player_id, COUNT(*) AS penalties\n    FROM penalties\n    WHERE game_id = '$game_id'\n    GROUP BY player_id\n  ) pen ON ps.player_id = pen.player_id\n  LEFT JOIN (\n    SELECT player_id, COUNT(*) AS dropgoals\n    FROM dropgoals\n    WHERE game_id = '$game_id'\n    GROUP BY player_id\n  ) dg ON ps.player_id = dg.player_id\nWHERE \n  ps.game_id = '$game_id'\nORDER BY \n  ps.number;