DELIMITER //\nCREATE PROCEDURE dynamic_search_replace(\n  IN table_name VARCHAR(255),\n  IN field_name VARCHAR(255),\n  IN search_text VARCHAR(255),\n  IN replace_text VARCHAR(255)\n)\nBEGIN\n  SET @sql = CONCAT('UPDATE ', table_name, ' SET ', field_name, ' = REPLACE(', field_name, ', ''', search_text, ''', ''', replace_text, ''');');\n  PREPARE stmt FROM @sql;\n  EXECUTE stmt;\n  DEALLOCATE PREPARE stmt;\nEND//\nDELIMITER ;