-- Server A ( range 1-10 )\nCREATE TABLE TableA (\n    ID INT IDENTITY(1,1) PRIMARY KEY,\n    Name VARCHAR(50)\n)\n\n-- Server B ( range 11-20 )\nCREATE TABLE TableB (\n    ID INT IDENTITY(11,1) PRIMARY KEY,\n    Name VARCHAR(50)\n)\n\n-- Insert 10 rows on Server A\nINSERT INTO TableA (Name) VALUES ('Name1')\nINSERT INTO TableA (Name) VALUES ('Name2')\n...\nINSERT INTO TableA (Name) VALUES ('Name10')\n\n-- Insert 10 rows on Server B\nINSERT INTO TableB (Name) VALUES ('Name11')\nINSERT INTO TableB (Name) VALUES ('Name12')\n...\nINSERT INTO TableB (Name) VALUES ('Name20')\n\n-- Replication occurs, and rows are inserted on the other server