CREATE PROCEDURE sp_SearchDocuments\n    @Title nvarchar(100) = NULL,\n    @Author nvarchar(100) = NULL,\n    @Description nvarchar(200) = NULL\nAS\nBEGIN\n    SELECT ID, Title, Author, Description, DateCreated\n    FROM Documents\n    WHERE\n        (@Title IS NULL OR Title LIKE '%' + @Title + '%') AND\n        (@Author IS NULL OR Author LIKE '%' + @Author + '%') AND\n        (@Description IS NULL OR Description LIKE '%' + @Description + '%')\nEND\nGO