BEGIN\n    FORALL i IN 1..your_array.COUNT SAVE EXCEPTIONS\n    INSERT INTO your_table (column1, column2)\n    VALUES (your_array(i).column1, your_array(i).column2);\n\n    EXCEPTION\n        WHEN OTHERS THEN\n            -- Log the exceptions\n            FOR j IN 1..SQL%BULK_EXCEPTIONS.COUNT LOOP\n                -- Log the error message and the row that caused the error\n                DBMS_OUTPUT.PUT_LINE(SQL%BULK_EXCEPTIONS(j).ERROR_MESSAGE);\n                DBMS_OUTPUT.PUT_LINE(your_array(SQL%BULK_EXCEPTIONS(j).ERROR_INDEX).column1);\n                DBMS_OUTPUT.PUT_LINE(your_array(SQL%BULK_EXCEPTIONS(j).ERROR_INDEX).column2);\n            END LOOP;\nEND;