CREATE PROCEDURE sp_SearchDocuments\n    @Title nvarchar(100) = NULL,\n    @Author nvarchar(100) = NULL,\n    @Description nvarchar(200) = NULL\nAS\nBEGIN\n    SELECT ID, Title, Author, Description, DateCreated\n    FROM Documents\n    WHERE\n        (ISNULL(COALESCE(@Title, Title), '') = ISNULL(Title, '')) AND\n        (ISNULL(COALESCE(@Author, Author), '') = ISNULL(Author, '')) AND\n        (ISNULL(COALESCE(@Description, Description), '') = ISNULL(Description, ''))\nEND\nGO