CREATE FUNCTION metaphone(@value VARCHAR(50))\nRETURNS VARCHAR(10)\nAS\nBEGIN\n  DECLARE @metaphone VARCHAR(10)\n  SET @metaphone = \n    UPPER(LEFT(@value, 1)) + \n    REPLACE(REPLACE(REPLACE(SUBSTRING(@value, 2, LEN(@value)), '[aeiou]', ''), '[bfpv]', 'B'), '[cgjkqsxz]', 'K')\n  RETURN @metaphone\nEND;\n\nSELECT \n  a.value, \n  b.value\nFROM your_table a\nJOIN your_table b ON metaphone(a.value) = metaphone(b.value) AND a.value <> b.value;