-- Assuming your table is named 'my_table' and the primary key column is 'id'\n\n-- Drop the primary key constraint\nALTER TABLE my_table DROP CONSTRAINT pk_my_table;\n\n-- Update the id column with a new, sequential value\nUPDATE my_table\nSET id = (SELECT ROW_NUMBER() OVER (ORDER BY id) FROM my_table AS t WHERE t.id = my_table.id);\n\n-- Re-create the primary key constraint\nALTER TABLE my_table ADD CONSTRAINT pk_my_table PRIMARY KEY (id);\n\n-- Re-seed the identity column (if applicable)\nDBCC CHECKIDENT ('my_table', RESEED, 0);