CREATE PROCEDURE SearchData\n    @Param1 INT = NULL,\n    @Param2 VARCHAR(50) = NULL,\n    ...\nAS\nBEGIN\n    DECLARE @sql NVARCHAR(MAX) = N'SELECT * FROM YourTable WHERE 1=1';\n\n    IF @Param1 IS NOT NULL\n        SET @sql += N' AND Column1 = @Param1';\n\n    IF @Param2 IS NOT NULL\n        SET @sql += N' AND Column2 LIKE @Param2';\n\n    ...\n\n    EXEC sp_executesql @sql, N'@Param1 INT, @Param2 VARCHAR(50), ...', @Param1, @Param2, ...\nEND