SELECT\n  serialnumber,\n  partnb,\n  MAX(CASE WHEN id='AGR' THEN actual END) as AGR,\n  MAX(CASE WHEN id='AGR' THEN nominal END) as AGR_nominal,\n  MAX(CASE WHEN id='APR' THEN actual END) as APR,\n  MAX(CASE WHEN id='APR' THEN nominal END) as APR_nominal,\n  MAX(CASE WHEN id='DCI' THEN actual END) as DCI,\n  MAX(CASE WHEN id='DCI' THEN nominal END) as DCI_nominal\nFROM YourTable\nGROUP BY serialnumber, partnb