CREATE TRIGGER trg_ForUpdateOnBookIssuedDetails\non BOOKISSUEDDETAILS\nFor update\nas\nbegin\n    declare @Rows1 int, @Rows2 int\n    IF EXISTS(\n        SELECT 1\n        FROM INSERTED i\n        INNER JOIN DELETED d ON i.BookId = d.BookId\n        WHERE i.ReturnedOn <> d.ReturnedOn OR (i.ReturnedOn IS NULL AND d.ReturnedOn IS NOT NULL) OR (i.ReturnedOn IS NOT NULL AND d.ReturnedOn IS NULL)\n    )\n    begin\n        -- rest of your trigger code\n    end\nend