CREATE TABLE A (\n  Aid NUMBER PRIMARY KEY\n);\n\nCREATE TABLE B (\n  Aid NUMBER,\n  Bnum NUMBER,\n  PRIMARY KEY (Aid, Bnum),\n  FOREIGN KEY (Aid) REFERENCES A(Aid)\n);\n\nCREATE TABLE C (\n  Aid NUMBER,\n  Cnum NUMBER,\n  PRIMARY KEY (Aid, Cnum),\n  FOREIGN KEY (Aid) REFERENCES A(Aid)\n);\n\nCREATE TABLE D (\n  Aid NUMBER,\n  Bnum NUMBER,\n  Cnum NUMBER,\n  PRIMARY KEY (Aid, Bnum, Cnum),\n  FOREIGN KEY (Aid, Bnum) REFERENCES B(Aid, Bnum),\n  FOREIGN KEY (Aid, Cnum) REFERENCES C(Aid, Cnum)\n);\n\nCREATE SEQUENCE C_Cnum_SEQ START WITH 1 INCREMENT BY 1;\nCREATE SEQUENCE D_Cnum_SEQ START WITH 1 INCREMENT BY 1;\n\nCREATE TRIGGER C_Insert_Trigger\nBEFORE INSERT ON C\nFOR EACH ROW\nBEGIN\n  SELECT C_Cnum_SEQ.NEXTVAL INTO :NEW.Cnum FROM DUAL;\nEND;\n\nCREATE TRIGGER D_Insert_Trigger\nBEFORE INSERT ON D\nFOR EACH ROW\nBEGIN\n  SELECT D_Cnum_SEQ.NEXTVAL INTO :NEW.Cnum FROM DUAL;\nEND;