SELECT video_name, \n       SUM(CASE WHEN tag_name = 'x' THEN 2 ELSE 1 END) AS score \nFROM videos \nINNER JOIN videotags ON videos.id=videotags.video_id \nINNER JOIN tags ON tags.id=videotags.tag_id \nWHERE tag_name IN ('x', 'y', 'z') \nAND videos.id <> 'current_video_id' \nGROUP BY videos.id \nORDER BY score DESC \nLIMIT 5;