CREATE PROCEDURE MyProc\n    (@TableName nvarchar(255), @FirstName nvarchar(50), @LastName nvarchar(50))\nAS\nBEGIN\n    DECLARE @SQLStatement nvarchar(max)\n    SET @SQLStatement = 'SELECT * FROM ' + QUOTENAME(@TableName) + ' WHERE FirstName = @FirstName AND LastName = @LastName'\n    EXEC sp_executesql @SQLStatement, N'@FirstName nvarchar(50), @LastName nvarchar(50)', @FirstName = @FirstName, @LastName = @LastName\nEND