DECLARE @sql nvarchar(max) = '';\nDECLARE @proc_name sysname = 'your_procedure_name';  -- replace with your procedure name\n\nDECLARE db_cursor CURSOR FOR \n    SELECT name \n    FROM sys.databases \n    WHERE database_id > 4  -- exclude system databases\n    ORDER BY name;\n\nDECLARE @db_name sysname;\n\nOPEN db_cursor;\nFETCH NEXT FROM db_cursor INTO @db_name;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @sql = 'SELECT ''' + @db_name + ''' AS database_name, name AS procedure_name \n                FROM ' + QUOTENAME(@db_name) + '.sys.procedures \n                WHERE name = ''' + @proc_name + '''';\n\n    EXEC sp_executesql @sql;\n\n    FETCH NEXT FROM db_cursor INTO @db_name;\nEND\n\nCLOSE db_cursor;\nDEALLOCATE db_cursor;