CREATE WORKLOAD GROUP [LowPriorityGroup]\nWITH (\n    GROUP_MAX_REQUESTS = 0,\n    REQUEST_MAX_CPU_TIME_SEC = 0,\n    REQUEST_MAX_MEMORY_GRANT_PERCENT = 0,\n    MAX_DOP = 0,\n    GROUP_PRIORITY = LOW\n);\n\nCREATE WORKLOAD CLASSIFIER [LowPriorityClassifier]\nWITH (\n    WORKLOAD_GROUP = 'LowPriorityGroup',\n    CLASSIFIER_FUNCTION = NULL\n);