-- Example of a well-structured PL/SQL procedure\nCREATE OR REPLACE PROCEDURE my_procedure(p_input IN VARCHAR2)\nAS\n  v_variable VARCHAR2(100);\nBEGIN\n  -- Do something with the input parameter\n  v_variable := p_input || ' modified';\n  DBMS_OUTPUT.PUT_LINE(v_variable);\nEXCEPTION\n  WHEN OTHERS THEN\n    -- Handle exceptions properly\n    DBMS_OUTPUT.PUT_LINE('Error occurred: ' || SQLERRM);\nEND;