DECLARE\n  v_table_name VARCHAR2(30) := 'YOUR_TABLE_NAME';\n  v_file_name VARCHAR2(30) := 'dump_' || v_table_name || '.sql';\n  v_file UTL_FILE.FILE_TYPE;\n  v_statement VARCHAR2(32767);\n\nBEGIN\n  v_file := UTL_FILE.FOPEN(v_file_name, 'w');\n  \n  FOR cur_rec IN (SELECT * FROM v_table_name) LOOP\n    v_statement := 'INSERT INTO ' || v_table_name || ' VALUES (';\n    \n    FOR i IN 1..cur_rec.COUNT LOOP\n      IF i > 1 THEN\n        v_statement := v_statement || ', ';\n      END IF;\n      \n      IF cur_rec(i) IS NULL THEN\n        v_statement := v_statement || 'NULL';\n      ELSE\n        v_statement := v_statement || '''' || cur_rec(i) || '''';\n      END IF;\n    END LOOP;\n    \n    v_statement := v_statement || ');';\n    \n    UTL_FILE.PUT_LINE(v_file, v_statement);\n  END LOOP;\n  \n  UTL_FILE.FCLOSE(v_file);\nEND;\n/