DECLARE @ImageData varbinary(max)\nSELECT @ImageData = ImageColumn\nFROM YourTable\nWHERE ImageColumn IS NOT NULL\n\nDECLARE @Path nvarchar(255) = 'C:\Path\To\Save\Image.jpg'\nDECLARE @FileStream INT\nEXEC sp_OACreate 'ADODB.Stream', @FileStream OUTPUT\nEXEC sp_OASetProperty @FileStream, 'Type', 1\nEXEC sp_OAMethod @FileStream, 'Open'\nEXEC sp_OAMethod @FileStream, 'Write', NULL, @ImageData\nEXEC sp_OAMethod @FileStream, 'SaveToFile', NULL, @Path, 2\nEXEC sp_OAMethod @FileStream, 'Close'\nEXEC sp_OADestroy @FileStream