CREATE TABLE tbTableOne (id INT, value VARCHAR(10));\nCREATE TABLE tbTableTwo (id INT, value VARCHAR(10));\n\nCREATE VIEW vwIndexedView WITH SCHEMABINDING AS\nSELECT t1.id, t1.value, t2.value AS value2\nFROM tbTableOne t1\nJOIN tbTableTwo t2 ON t1.id = t2.id;\n\nCREATE UNIQUE CLUSTERED INDEX idx_vwIndexedView ON vwIndexedView (id);\n\nBEGIN TRANSACTION testTransaction\nINSERT INTO tbTableOne VALUES (1, '2');\nSELECT * FROM vwIndexedView; -- returns the updated data\nCOMMIT TRANSACTION;\n\nSELECT * FROM vwIndexedView; -- returns the same updated data