SELECT *\nFROM (\n    SELECT ROW_NUMBER() OVER (ORDER BY hrl.Frn) as RowNumber,\n           hrl.unq, hrl.LcnsId, hc.Business, hc.Name, hc.Phone,\n           hrl.Frn, hrl.CallSign, hrl.gsamarkettypeid,\n           gmt.[Market Type Code] + ' - ' + gmt.gsamarkettype,\n           hrl.gsalatitude, hrl.gsalongitude,\n           rsc.RadioServiceCode + ' - ' + rsc.RadioService,\n           GrantDt, ExpirationDt, EffectiveDt,\n           CancellationDt\n    FROM dbo.sbi_f_HldrRgstrtnLcns hrl\n    INNER JOIN dbo.sbi_f_HldrCntcts hc on hc.CallSign = hrl.CallSign\n    INNER JOIN dbo.sbi_l_radioservicecodes rsc on rsc.radioservicecodeid = hrl.radioservicecodeid\n    LEFT OUTER JOIN dbo.sbi_l_GSAMarketTypes gmt on gmt.GSAMarketTypeId = hrl.GSAMarketTypeId\n    WHERE hc.Entity_Type = 'L'  AND hrl.LicenseStatusId IN (1)\n) AS subquery\nWHERE RowNumber >= 1 AND RowNumber <= 20;